import { useEffect, useState } from "react";
import axios from "axios";

export default function AlternateLeaveApproval() {

  const user = JSON.parse(localStorage.getItem("erpUser"));
  const [leaves, setLeaves] = useState([]);

  const loadLeaves = async () => {
    try {
      const res = await axios.get(
  "http://localhost:5000/api/leave/pending",
  {
    params: {
      role: "Faculty",
      facultyId: user.userId
    }
  }
);
      setLeaves(res.data);
    } catch {
      alert("Error loading alternate approvals");
    }
  };

  useEffect(() => {
    loadLeaves();
  }, []);

  const approve = async (id) => {
    await axios.post(
      `http://localhost:5000/api/leave/approve/${id}`
    );
    alert("Approved & sent to HOD");
    loadLeaves();
  };

  const reject = async (id) => {
    await axios.post(
      `http://localhost:5000/api/leave/reject/${id}`
    );
    alert("Leave rejected");
    loadLeaves();
  };

  return (
    <div className="card">
      <h3>Alternate Faculty Leave Approval</h3>

      {leaves.length === 0 ? (
        <p>No pending adjustments</p>
      ) : (
        <table className="timetable">
          <thead>
            <tr>
              <th>Faculty</th>
              <th>From</th>
              <th>To</th>
              <th>Reason</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {leaves.map(l => (
              <tr key={l._id}>
                <td>{l.facultyName}</td>
                <td>{l.fromDate}</td>
                <td>{l.toDate}</td>
                <td>{l.reason}</td>
                <td>
                  <button onClick={() => approve(l._id)}>Approve</button>
                  <button onClick={() => reject(l._id)}>Reject</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
