import { useState } from "react";
import "../styles/app.css";

export default function StudentAttendance() {
  const timetableData = [
    { day: "Monday", hour: 2, subject: "DBMS", class: "CSE II A" },
    { day: "Monday", hour: 4, subject: "OS", class: "CSE II A" }
  ];

  const [department, setDepartment] = useState("");
  const [year, setYear] = useState("");
  const [subject, setSubject] = useState("");
  const [hours, setHours] = useState([]);

  // When subject changes → filter hours
  const handleSubjectChange = (sub) => {
    setSubject(sub);

    const filteredHours = timetableData
      .filter(t => t.subject === sub)
      .map(t => t.hour);

    setHours(filteredHours);
  };

  return (
    <div className="attendance-card">
      <h2>Student Attendance</h2>

      <div className="form-row">
        <label>Department</label>
        <select onChange={e => setDepartment(e.target.value)}>
          <option value="">Select</option>
          <option value="CSE">CSE</option>
          <option value="IT">IT</option>
        </select>
      </div>

      <div className="form-row">
        <label>Year</label>
        <select onChange={e => setYear(e.target.value)}>
          <option value="">Select</option>
          <option value="2">II</option>
          <option value="3">III</option>
        </select>
      </div>

      <div className="form-row">
        <label>Subject (From My Timetable)</label>
        <select onChange={e => handleSubjectChange(e.target.value)}>
          <option value="">Select</option>
          {[...new Set(timetableData.map(t => t.subject))].map(sub => (
            <option key={sub}>{sub}</option>
          ))}
        </select>
      </div>

      <div className="form-row">
        <label>Hour (Auto)</label>
        <select>
          <option value="">Select Hour</option>
          {hours.map(h => (
            <option key={h}>{h}</option>
          ))}
        </select>
      </div>

      <button className="primary-btn" disabled={!subject}>
        Mark Attendance
      </button>
    </div>
  );
}
