import { useEffect, useState } from "react";
import axios from "axios";
import "../styles/leave.css";

/* ===== HELPER: GET DAY NAME ===== */
const getDayName = (dateStr) =>
  new Date(dateStr).toLocaleDateString("en-US", { weekday: "long" });

export default function LeaveApply({ remainingCL }) {

  const user = JSON.parse(localStorage.getItem("erpUser"));

  const [leaveType, setLeaveType] = useState("");
  const [fromDate, setFromDate] = useState("");
  const [toDate, setToDate] = useState("");
  const [session, setSession] = useState("");
  const [reason, setReason] = useState("");

  const [classes, setClasses] = useState([]);
  const [facultyList, setFacultyList] = useState([]);
  const [showAdjustment, setShowAdjustment] = useState(false);

  /* ===== LOAD FACULTY LIST FOR ADJUSTMENT ===== */
  useEffect(() => {
    axios.get("http://localhost:5000/api/users")
      .then(res => setFacultyList(res.data))
      .catch(() => setFacultyList([]));
  }, []);

  /* =========================
     FINAL SUBMIT TO DB
     ========================= */
  const submitLeave = async (adjustments) => {
    try {
      await axios.post("http://localhost:5000/api/leave/apply", {
        facultyId: user.userId,
        facultyName: user.username,
        designation: user.designation,

        leaveType,
        fromDate,
        toDate,
        session,
        reason,

        adjustments,
        status: "Pending-HOD"
      });

      alert("Leave applied successfully and sent for approval");

      // reset UI
      setShowAdjustment(false);
      setClasses([]);
      setLeaveType("");
      setFromDate("");
      setToDate("");
      setSession("");
      setReason("");

    } catch (err) {
      alert(err.response?.data?.message || "Error applying leave");
    }
  };

  /* =========================
     APPLY LEAVE (CHECK ADJUSTMENT)
     ========================= */
  const applyLeave = async () => {

    if (!leaveType || !fromDate || !toDate || !session || !reason) {
      alert("All fields are required");
      return;
    }

    if (leaveType === "CL" && remainingCL <= 0) {
      alert("CL not available. Please select LOP.");
      return;
    }

    // 1️⃣ Get faculty timetable
    const res = await axios.get(
      `http://localhost:5000/api/timetable/faculty/${user.userId}`
    );
    const timetable = res.data;

    const affectedClasses = [];

    // 2️⃣ Date loop
    const start = new Date(fromDate);
    const end = new Date(toDate);

    for (let d = new Date(start); d <= end; d.setDate(d.getDate() + 1)) {
      const dayName = getDayName(d.toISOString().slice(0, 10));

      // session-based hours
      let validHours = [];
      if (session === "FN") validHours = [1, 2, 3, 4];
      else if (session === "AN") validHours = [5, 6, 7];
      else validHours = [1, 2, 3, 4, 5, 6, 7];

      timetable.forEach(t => {
        if (t.day === dayName && validHours.includes(t.hour)) {
          affectedClasses.push({
            date: d.toISOString().slice(0, 10),
            hour: t.hour,
            subject: t.subject,
            section: `${t.department} ${t.year} ${t.section}`,
            adjustedFacultyId: ""
          });
        }
      });
    }

    // 3️⃣ Decide adjustment
    if (affectedClasses.length > 0) {
      setClasses(affectedClasses);
      setShowAdjustment(true);
    } else {
      submitLeave([]); // no adjustment needed
    }
  };

  return (
    <div className="leave-card">

      <h2 className="leave-heading">Leave Application</h2>

      {/* ===== FORM ===== */}
      <div className="leave-form-grid">

        <div>
          <label>Leave Type</label>
          <select value={leaveType} onChange={e => setLeaveType(e.target.value)}>
            <option value="">Select</option>
            <option value="CL">Casual Leave (CL)</option>
            <option value="LOP">Loss of Pay (LOP)</option>
          </select>
          <small>Remaining CL: {remainingCL}</small>
        </div>

        <div>
          <label>Session</label>
          <select value={session} onChange={e => setSession(e.target.value)}>
            <option value="">Select</option>
            <option>FN</option>
            <option>AN</option>
            <option>Full Day</option>
          </select>
        </div>

        <div>
          <label>From Date</label>
          <input type="date" value={fromDate} onChange={e => setFromDate(e.target.value)} />
        </div>

        <div>
          <label>To Date</label>
          <input type="date" value={toDate} onChange={e => setToDate(e.target.value)} />
        </div>

        <div className="full-width">
          <label>Reason</label>
          <textarea
            rows="3"
            value={reason}
            placeholder="Enter reason for leave"
            onChange={e => setReason(e.target.value)}
          />
        </div>

      </div>

      <div className="leave-btn-row">
        <button className="btn-primary" onClick={applyLeave}>
          Apply Leave
        </button>
      </div>

      {/* ===== ADJUSTMENT ===== */}
      {showAdjustment && (
        <div className="adjustment-section">

          <h3>Class Adjustment Required</h3>

          <table className="adjustment-table">
            <thead>
              <tr>
                <th>Date</th>
                <th>Hour</th>
                <th>Subject</th>
                <th>Section</th>
                <th>Adjust Faculty</th>
              </tr>
            </thead>
            <tbody>
              {classes.map((c, i) => (
                <tr key={i}>
                  <td>{c.date}</td>
                  <td>{c.hour}</td>
                  <td>{c.subject}</td>
                  <td>{c.section}</td>
                  <td>
                    <select
                      onChange={e => c.adjustedFacultyId = e.target.value}
                    >
                      <option value="">Select</option>
                      {facultyList.map(f => (
                        <option key={f.userId} value={f.userId}>
                          {f.username}
                        </option>
                      ))}
                    </select>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          <div className="leave-btn-row">
            <button
              className="btn-primary"
              onClick={() => submitLeave(classes)}
            >
              Submit with Adjustment
            </button>
          </div>

        </div>
      )}
    </div>
  );
}
