import { useEffect, useState } from "react";
import axios from "axios";
import "../styles/dashboard.css";

export default function StudentAttendance() {

  const facultyId = "F001"; // later from login
  const [timetable, setTimetable] = useState([]);
  const [hours, setHours] = useState([]);
  const [today, setToday] = useState("");

  // Convert system date → Day name
  useEffect(() => {
    const days = [
      "Sunday",
      "Monday",
      "Tuesday",
      "Wednesday",
      "Thursday",
      "Friday",
      "Saturday"
    ];

    const systemDay = days[new Date().getDay()];
    setToday(systemDay);
  }, []);

  // Fetch faculty timetable
  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/timetable/faculty/${facultyId}`)
      .then(res => setTimetable(res.data));
  }, []);

  // Filter hours based on today's day
  useEffect(() => {
    if (!today) return;

    const todayClasses = timetable.filter(t => t.day === today);

    let result = [];

    todayClasses.forEach(entry => {
      for (let i = entry.periodStart; i <= entry.periodEnd; i++) {
        result.push({
          period: i,
          subject: entry.subject
        });
      }
    });

    setHours(result);
  }, [today, timetable]);

  return (
    <div className="card">
      <h3>Student Attendance</h3>

      <p style={{ fontWeight: "bold" }}>
        Today : {today}
      </p>

      {hours.length > 0 ? (
        <table className="timetable" style={{ marginTop: 20 }}>
          <thead>
            <tr>
              <th>Hour</th>
              <th>Subject</th>
              <th>Mark Attendance</th>
            </tr>
          </thead>
          <tbody>
            {hours.map((h, index) => (
              <tr key={index}>
                <td>{h.period}</td>
                <td>{h.subject}</td>
                <td>
                  <button className="btn-primary">Mark</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p style={{ marginTop: 20, color: "red" }}>
          No classes scheduled today
        </p>
      )}
    </div>
  );
}
