import { useEffect, useState } from "react";
import axios from "axios";
import "../styles/dashboard.css";

export default function StudentAttendance() {

  /* ===== Logged-in faculty ===== */
  const user = JSON.parse(localStorage.getItem("erpUser"));
  const facultyId = user?.userId;

  /* ===== State ===== */
  const [slots, setSlots] = useState([]);
  const [selectedSlot, setSelectedSlot] = useState(null);
  const [students, setStudents] = useState([]);
  const [attendance, setAttendance] = useState({});
  const [loading, setLoading] = useState(false);
  const [locked, setLocked] = useState(false);

  /* ===== Date info ===== */
  const todayDate = new Date().toISOString().slice(0, 10);
  const todayDay = new Date().toLocaleDateString("en-US", {
    weekday: "long"
  });

  /* =========================================================
     LOAD TODAY'S TIMETABLE HOURS FOR FACULTY
     ========================================================= */
  useEffect(() => {
    if (!facultyId) return;

    axios
      .get(`http://localhost:5000/api/attendance/hours/${facultyId}`)
      .then(res => setSlots(res.data))
      .catch(() => alert("Failed to load timetable"));
  }, [facultyId]);

  /* =========================================================
     LOAD STUDENTS FOR SELECTED SLOT
     ========================================================= */
  const loadStudents = async slot => {
    if (locked) return;

    setSelectedSlot(slot);
    setAttendance({});
    setLoading(true);

    try {
      const res = await axios.get(
        "http://localhost:5000/api/attendance/students",
        {
          params: {
            department: slot.department,
            year: slot.year,
            section: slot.section
          }
        }
      );
      setStudents(res.data);
    } catch {
      alert("Failed to load students");
    }

    setLoading(false);
  };

  /* =========================================================
     SUBMIT ATTENDANCE
     ========================================================= */
  const submitAttendance = async () => {
    if (!selectedSlot) return;

    const records = students.map(stu => ({
      date: todayDate,
      day: todayDay,
      hour: selectedSlot.hour,
      facultyId,
      department: selectedSlot.department,
      year: selectedSlot.year,
      section: selectedSlot.section,
      subject: selectedSlot.subject,
      registerNo: stu.registerNo,
      status: attendance[stu.registerNo] || "Present"
    }));

    try {
      await axios.post(
        "http://localhost:5000/api/attendance/mark",
        records
      );

      alert("Attendance submitted successfully");
      setLocked(true);
      setSelectedSlot(null);
      setStudents([]);

    } catch (err) {
      if (err.response) {
        alert(err.response.data.message);
        if (err.response.status === 409) {
          setLocked(true);
        }
      } else {
        alert("Server not reachable");
      }
    }
  };

  /* =========================================================
     UI
     ========================================================= */
  return (
    <div className="card">
      <h3>Student Attendance – {todayDay}</h3>

      {/* ===== HOUR BUTTONS ===== */}
      <div style={{ marginBottom: 15 }}>
        {slots.map((s, i) => (
          <button
            key={i}
            onClick={() => loadStudents(s)}
            disabled={locked}
            style={{
              marginRight: 8,
              background: locked ? "#ccc" : "#007bff",
              color: "white"
            }}
          >
            Hour {s.hour} – {s.subject}
          </button>
        ))}
      </div>

      {locked && (
        <p style={{ color: "red" }}>
          Attendance already submitted for today
        </p>
      )}

      {/* ===== STUDENT TABLE ===== */}
      {loading && <p>Loading students...</p>}

      {students.length > 0 && !locked && (
        <>
          <table className="timetable">
            <thead>
              <tr>
                <th>Register No</th>
                <th>Name</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {students.map(stu => (
                <tr key={stu.registerNo}>
                  <td>{stu.registerNo}</td>
                  <td>{stu.name}</td>
                  <td>
                    <select
                      onChange={e =>
                        setAttendance({
                          ...attendance,
                          [stu.registerNo]: e.target.value
                        })
                      }
                    >
                      <option value="Present">Present</option>
                      <option value="Absent">Absent</option>
                    </select>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          <button
            onClick={submitAttendance}
            className="btn-primary"
          >
            Submit Attendance
          </button>
        </>
      )}
    </div>
  );
}
