import { useState } from "react";
import "../styles/dashboard.css";

export default function LeaveApply() {
  const [leaveType, setLeaveType] = useState("");
  const [date, setDate] = useState("");
  const [session, setSession] = useState("");
  const [showAdjustment, setShowAdjustment] = useState(false);

  // sample values – later from DB
  const availableCL = 0;

  const timetableToday = [
    { hour: 5, subject: "DBMS", section: "CSE II A" },
    { hour: 6, subject: "DBMS", section: "CSE II A" },
    { hour: 7, subject: "DBMS", section: "CSE II A" }
  ];

  const handleLeaveTypeChange = (value) => {
    if (leaveType === "CL" && remainingCL <= 0) {
  alert("CL not available. Please select LOP.");
  return;
}
    setLeaveType(value);
  };

  const handleApply = () => {
    if (!leaveType || !date || !session) {
      alert("Please fill all fields");
      return;
    }

    if (timetableToday.length > 0) {
      setShowAdjustment(true);
    } else {
      alert("Leave applied successfully. No class adjustment required.");
    }
  };

  return (
    <div className="leave-container">

      <h3 className="leave-title">Leave Application</h3>

      {/* FORM */}
      <div className="leave-form">

        <div className="leave-row">
          <label>Leave Type</label>
          <select onChange={(e) => handleLeaveTypeChange(e.target.value)}>
            <option value="">Select</option>
            <option value="CL">Casual Leave (CL)</option>
            <option value="LOP">Loss of Pay (LOP)</option>
          </select>
        </div>

        <div className="leave-row">
          <label>Date</label>
          <input type="date" onChange={(e) => setDate(e.target.value)} />
        </div>

        <div className="leave-row">
          <label>Session</label>
          <select onChange={(e) => setSession(e.target.value)}>
            <option value="">Select</option>
            <option>FN</option>
            <option>AN</option>
            <option>Full Day</option>
          </select>
        </div>

        <div className="leave-actions">
          <button className="btn-primary" onClick={handleApply}>
            Apply Leave
          </button>
        </div>
      </div>

      {/* ADJUSTMENT SECTION */}
      {showAdjustment && (
        <div className="adjustment-box">

          <h4>Class Adjustment Required</h4>

          <table className="timetable">
            <thead>
              <tr>
                <th>Hour</th>
                <th>Subject</th>
                <th>Section</th>
                <th>Adjusting Faculty</th>
              </tr>
            </thead>
            <tbody>
              {timetableToday.map((t, i) => (
                <tr key={i}>
                  <td>{t.hour}</td>
                  <td>{t.subject}</td>
                  <td>{t.section}</td>
                  <td>
                    <select>
                      <option>Select Faculty</option>
                      <option>Dr. Anand</option>
                      <option>Ms. Priya</option>
                      <option>Mr. Ramesh</option>
                    </select>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          <div className="leave-actions">
            <button className="btn-primary">
              Submit Leave with Adjustment
            </button>
          </div>
        </div>
      )}

    </div>
  );
}
