import { useState } from "react";

const getDayFromDate = (dateStr) => {
  const days = ["Sunday","Monday","Tuesday","Wednesday","Thursday","Friday","Saturday"];
  return days[new Date(dateStr).getDay()];
};

export default function LeaveApply({ remainingCL, applyLeave }) {

  const [leaveType, setLeaveType] = useState("");
  const [date, setDate] = useState("");
  const [session, setSession] = useState("");

  const handleApply = () => {
    if (!leaveType || !date || !session) {
      alert("Please fill all fields");
      return;
    }

    if (leaveType === "CL" && remainingCL <= 0) {
      alert("CL not available. Select LOP.");
      return;
    }

    applyLeave({
      leaveType,
      date,
      session,
      day: getDayFromDate(date)
    });
  };

  return (
    <div className="leave-form">

      <div className="leave-row">
        <label>Leave Type</label>
        <select onChange={(e) => setLeaveType(e.target.value)}>
          <option value="">Select</option>
          <option value="CL">Casual Leave (CL)</option>
          <option value="LOP">Loss of Pay (LOP)</option>
        </select>
      </div>

      <div className="leave-row">
        <label>Date</label>
        <input type="date" onChange={(e) => setDate(e.target.value)} />
      </div>

      <div className="leave-row">
        <label>Session</label>
        <select onChange={(e) => setSession(e.target.value)}>
          <option value="">Select</option>
          <option>FN</option>
          <option>AN</option>
          <option>Full Day</option>
        </select>
      </div>

      <div className="leave-actions">
        <button className="btn-primary" onClick={handleApply}>
          Apply Leave
        </button>
      </div>
    </div>
  );
}
