import { useEffect, useState } from "react";
import axios from "axios";
import "../styles/dashboard.css";

export default function HODLeaveApproval() {

  const user = JSON.parse(localStorage.getItem("erpUser"));
  const department = user?.department;

  const [leaves, setLeaves] = useState([]);
  const [loading, setLoading] = useState(true);

  /* =========================
     LOAD HOD PENDING LEAVES
     ========================= */
  const loadLeaves = async () => {
    try {
      const res = await axios.get(
        "http://localhost:5000/api/leave/pending",
        {
          params: {
            role: "HOD",
            department
          }
        }
      );
      setLeaves(res.data);
    } catch (err) {
      alert("Error fetching leaves");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadLeaves();
  }, []);

  /* =========================
     APPROVE / REJECT
     ========================= */
  const handleAction = async (id, action) => {
    try {
      const url =
        action === "approve"
          ? `/api/leave/approve/${id}`
          : `/api/leave/reject/${id}`;

      await axios.post(`http://localhost:5000${url}`);
      alert(`Leave ${action}d successfully`);
      loadLeaves();

    } catch (err) {
      alert("Action failed");
    }
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div className="card">
      <h3>HOD Leave Approval</h3>

      {leaves.length === 0 ? (
        <p>No pending leave approvals</p>
      ) : (
        leaves.map(leave => (
          <div key={leave._id} className="card" style={{ marginBottom: 15 }}>

            {/* LEAVE FACULTY */}
            <p>
              <strong>Leave Faculty:</strong> {leave.facultyName}
            </p>

            {/* ADJUSTMENT DETAILS */}
            <table className="timetable">
              <thead>
                <tr>
                  <th>Altered Faculty</th>
                  <th>Year</th>
                  <th>Hour</th>
                </tr>
              </thead>
              <tbody>
                {leave.adjustments.map((a, i) => (
                  <tr key={i}>
                    <td>{a.adjustedFacultyId}</td>
                    <td>{a.section.split(" ")[1]}</td>
                    <td>{a.hour}</td>
                  </tr>
                ))}
              </tbody>
            </table>

            {/* ACTION BUTTONS */}
            <div style={{ marginTop: 10 }}>
              <button
                className="btn-primary"
                onClick={() => handleAction(leave._id, "approve")}
              >
                Approve
              </button>

              <button
                className="btn-danger"
                style={{ marginLeft: 10 }}
                onClick={() => handleAction(leave._id, "reject")}
              >
                Reject
              </button>
            </div>

          </div>
        ))
      )}
    </div>
  );
}
