import { useState } from "react";
import "../styles/dashboard.css";
import StudentAttendance from "../components/StudentAttendance";

export default function FacultyDashboard() {
  const [page, setPage] = useState("dashboard");

  return (
    <div className="erp-layout">

      {/* SIDEBAR */}
      <aside className="erp-sidebar">
        <h2 className="erp-logo">EduERP</h2>

        <ul>
          <li onClick={() => setPage("dashboard")}>Dashboard</li>
          <li onClick={() => setPage("biometric")}>Biometric</li>
          <li onClick={() => setPage("timetable")}>My Timetable</li>
          <li onClick={() => setPage("attendance")}>Student Attendance</li>
          <li>Reports</li>
          <li>Logout</li>
        </ul>
      </aside>

      {/* MAIN AREA */}
      <div className="erp-main">

        {/* HEADER */}
        <header className="erp-header">
          <h3>Faculty Dashboard</h3>
          <span>Welcome, Faculty</span>
        </header>

        {/* CONTENT */}
        <div className="erp-content">

          {page === "dashboard" && (
            <div className="dashboard-grid">
              <div className="card">Profile</div>
              <div className="card">Todayâ€™s Timetable</div>
              <div className="card">Notice Board</div>
            </div>
          )}

          {page === "attendance" && <StudentAttendance />}

          {page === "biometric" && <div className="card">Biometric Data</div>}

          {page === "timetable" && <div className="card">My Timetable</div>}

        </div>
      </div>
    </div>
  );
}
