import { useState } from "react";
import "../styles/app.css";

export default function StudentAttendance() {

  // Sample timetable (later from backend)
  const timetableData = [
    { subject: "DBMS", hour: 2 },
    { subject: "OS", hour: 4 }
  ];

  const [department, setDepartment] = useState("CSE");
  const [year, setYear] = useState("II");
  const [subject, setSubject] = useState("");
  const [hour, setHour] = useState("");

  const subjects = [...new Set(timetableData.map(t => t.subject))];

  const handleSubjectChange = (sub) => {
    setSubject(sub);
    const h = timetableData.find(t => t.subject === sub)?.hour || "";
    setHour(h);
  };

  return (
    <div className="attendance-card-horizontal">
      <h2>Student Attendance</h2>

      <div className="row">
        <label>Department</label>
        <select value={department} onChange={e => setDepartment(e.target.value)}>
          <option>CSE</option>
          <option>IT</option>
        </select>
      </div>

      <div className="row">
        <label>Year</label>
        <select value={year} onChange={e => setYear(e.target.value)}>
          <option>II</option>
          <option>III</option>
        </select>
      </div>

      <div className="row">
        <label>Subject</label>
        <select value={subject} onChange={e => handleSubjectChange(e.target.value)}>
          <option value="">Select</option>
          {subjects.map(sub => (
            <option key={sub} value={sub}>{sub}</option>
          ))}
        </select>
      </div>

      <div className="row">
        <label>Hour</label>
        <select value={hour} disabled>
          <option>{hour}</option>
        </select>
      </div>

      <div className="row button-row">
        <button className="primary-btn">Mark Attendance</button>
      </div>
    </div>
  );
}
