import { useEffect, useState } from "react";
import axios from "axios";
import "../styles/dashboard.css";

export default function StudentAttendance() {
  const user = JSON.parse(localStorage.getItem("erpUser"));
  const facultyId = user.userId;

  const [slots, setSlots] = useState([]);
  const [selected, setSelected] = useState(null);
  const [students, setStudents] = useState([]);
  const [attendance, setAttendance] = useState({});

  const today = new Date().toISOString().slice(0, 10);
  const day = new Date().toLocaleDateString("en-US", { weekday: "long" });

  /* ===== LOAD TODAY TIMETABLE HOURS ===== */
  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/attendance/hours/${facultyId}`)
      .then(res => setSlots(res.data));
  }, [facultyId]);

  /* ===== LOAD STUDENTS ===== */
  const loadStudents = slot => {
    setSelected(slot);

    axios
      .get("http://localhost:5000/api/attendance/students", {
        params: {
          department: slot.department,
          year: slot.year,
          section: slot.section
        }
      })
      .then(res => setStudents(res.data));
  };

  /* ===== MARK ATTENDANCE ===== */
  const submitAttendance = async () => {
    const records = students.map(s => ({
      date: today,
      day,
      hour: selected.hour,
      facultyId,
      department: selected.department,
      year: selected.year,
      section: selected.section,
      subject: selected.subject,
      registerNo: s.registerNo,
      status: attendance[s.registerNo] || "Present"
    }));

    await axios.post("http://localhost:5000/api/attendance/mark", records);
    alert("Attendance saved successfully");
  };

  return (
    <div className="card">
      <h3>Student Attendance – {day}</h3>

      {/* HOURS */}
      <div style={{ marginBottom: 10 }}>
        {slots.map((s, i) => (
          <button
            key={i}
            onClick={() => loadStudents(s)}
            style={{ marginRight: 8 }}
          >
            Hour {s.hour} – {s.subject}
          </button>
        ))}
      </div>

      {/* STUDENT LIST */}
      {students.length > 0 && (
        <>
          <table className="timetable">
            <thead>
              <tr>
                <th>Register No</th>
                <th>Name</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {students.map(s => (
                <tr key={s.registerNo}>
                  <td>{s.registerNo}</td>
                  <td>{s.name}</td>
                  <td>
                    <select
                      onChange={e =>
                        setAttendance({
                          ...attendance,
                          [s.registerNo]: e.target.value
                        })
                      }
                    >
                      <option value="Present">Present</option>
                      <option value="Absent">Absent</option>
                    </select>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          <button onClick={submitAttendance} className="btn-primary">
            Submit Attendance
          </button>
        </>
      )}
    </div>
  );
}
