import { useEffect, useState } from "react";
import axios from "axios";
import "../styles/dashboard.css";

export default function LeaveCheck() {

  const user = JSON.parse(localStorage.getItem("erpUser"));
  const [summary, setSummary] = useState(null);
  const [loading, setLoading] = useState(true);

  const loadSummary = async () => {
    try {
      const res = await axios.get(
        `http://localhost:5000/api/leave/summary/${user.userId}`
      );

      setSummary(res.data);
    } catch (err) {
      alert("Error loading leave summary");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadSummary();
  }, []);

  const cancelLeave = async (id) => {
    if (!window.confirm("Cancel this leave?")) return;

    try {
      const res = await axios.delete(
        `http://localhost:5000/api/leave/cancel/${id}`
      );
      alert(res.data.message);
      loadSummary();
    } catch (err) {
      alert(err.response?.data?.message || "Cancel failed");
    }
  };

  if (loading) return <p>Loading...</p>;
  if (!summary) return <p>No data</p>;

  return (
    <div className="card">
      <h3>Leave Summary</h3>

      <table className="summary-table">
        <tbody>
          <tr><td>Total CL</td><td>{summary.totalCL}</td></tr>
          <tr><td>Used CL</td><td>{summary.usedCL}</td></tr>
          <tr><td>Remaining CL</td><td>{summary.remainingCL}</td></tr>
        </tbody>
      </table>

      <h4 style={{ marginTop: 20 }}>Leave History</h4>

      {summary.leaves.length === 0 ? (
        <p>No leave records</p>
      ) : (
        <table className="timetable">
          <thead>
            <tr>
              <th>From</th>
              <th>To</th>
              <th>Type</th>
              <th>Status</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {summary.leaves.map(l => (
              <tr key={l._id}>
                <td>{l.fromDate}</td>
                <td>{l.toDate}</td>
                <td>{l.leaveType}</td>
                <td>{l.status}</td>
                <td>
                  {l.status === "Pending" && (
                    <button
                      className="btn-danger"
                      onClick={() => cancelLeave(l._id)}
                    >
                      Cancel
                    </button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
