import { useEffect, useState } from "react";
import axios from "axios";
import "../styles/dashboard.css";

export default function FacultyList() {
  const [faculty, setFaculty] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/users/faculty")
      .then(res => setFaculty(res.data))
      .catch(err => console.error(err));
  }, []);

  return (
    <div className="card">
      <h3>Faculty List</h3>

      {faculty.length === 0 ? (
        <p>No faculty records found</p>
      ) : (
        <table className="timetable">
          <thead>
            <tr>
              <th>Faculty ID</th>
              <th>Name</th>
              <th>Department</th>
              <th>Email</th>
              <th>Mobile</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {faculty.map((f, index) => (
              <tr key={index}>
                <td>{f.userId}</td>
                <td>{f.name}</td>
                <td>{f.department}</td>
                <td>{f.email}</td>
                <td>{f.mobile}</td>
                <td>
                  <span
                    style={{
                      color: f.status === "Active" ? "green" : "red",
                      fontWeight: "bold"
                    }}
                  >
                    {f.status}
                  </span>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
