import { useEffect, useState } from "react";
import axios from "axios";
import "../styles/timetable.css";

export default function FacultyTimetable() {
  const [timetable, setTimetable] = useState([]);
  const [loading, setLoading] = useState(true);

  const user = JSON.parse(localStorage.getItem("erpUser"));
  const facultyId = user?.userId;

  useEffect(() => {
    if (!facultyId) return;

    axios
      .get(`http://localhost:5000/api/timetable/faculty/${facultyId}`)
      .then(res => {
        setTimetable(res.data);
        setLoading(false);
      })
      .catch(err => {
        console.error(err);
        setLoading(false);
      });
  }, [facultyId]);

  if (loading) return <p>Loading timetable...</p>;

  if (timetable.length === 0)
    return <p>No timetable assigned</p>;

  return (
    <div className="card">
      <h3>My Timetable</h3>

      <table className="timetable">
        <thead>
          <tr>
            <th>Day</th>
            <th>Hour</th>
            <th>Subject</th>
            <th>Class</th>
          </tr>
        </thead>
        <tbody>
          {timetable.map((t, i) => (
            <tr key={i}>
              <td>{t.day}</td>
              <td>{t.hour}</td>
              <td>{t.subject}</td>
              <td>
                {t.department} {t.year} {t.section}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
