import { useEffect, useState } from "react";
import axios from "axios";
import "../styles/dashboard.css";

export default function FacultyList() {
  const [faculty, setFaculty] = useState([]);
  const [showForm, setShowForm] = useState(false);

  const [form, setForm] = useState({
    userId: "",
    name: "",
    department: "",
    email: "",
    mobile: ""
  });

  /* ===== FETCH FACULTY ===== */
  const loadFaculty = () => {
    axios
      .get("http://localhost:5000/api/users/faculty")
      .then(res => setFaculty(res.data))
      .catch(err => console.error(err));
  };

  useEffect(() => {
    loadFaculty();
  }, []);

  /* ===== CREATE FACULTY ===== */
  const handleCreate = async () => {
    if (!form.userId || !form.name || !form.department) {
      alert("Faculty ID, Name and Department are required");
      return;
    }

    try {
      await axios.post("http://localhost:5000/api/users/create-faculty", form);
      alert("Faculty created successfully");
      setShowForm(false);
      setForm({
        userId: "",
        name: "",
        department: "",
        email: "",
        mobile: ""
      });
      loadFaculty(); // refresh list
    } catch (err) {
      alert(err.response?.data?.message || "Error creating faculty");
    }
  };

  return (
    <div className="card">
      <div style={{ display: "flex", justifyContent: "space-between" }}>
        <h3>Faculty List</h3>
        <button className="btn-primary" onClick={() => setShowForm(!showForm)}>
          + Create Faculty
        </button>
      </div>

      {/* ===== CREATE FORM ===== */}
      {showForm && (
        <div style={{ margin: "15px 0" }}>
          <input
            placeholder="Faculty ID (Eg: F001)"
            value={form.userId}
            onChange={e => setForm({ ...form, userId: e.target.value })}
          />
          <input
            placeholder="Faculty Name"
            value={form.name}
            onChange={e => setForm({ ...form, name: e.target.value })}
          />
          <input
            placeholder="Department"
            value={form.department}
            onChange={e => setForm({ ...form, department: e.target.value })}
          />
          <input
            placeholder="Email"
            value={form.email}
            onChange={e => setForm({ ...form, email: e.target.value })}
          />
          <input
            placeholder="Mobile"
            value={form.mobile}
            onChange={e => setForm({ ...form, mobile: e.target.value })}
          />

          <button className="btn-primary" onClick={handleCreate}>
            Save Faculty
          </button>
        </div>
      )}

      {/* ===== FACULTY TABLE ===== */}
      {faculty.length === 0 ? (
        <p>No faculty records found</p>
      ) : (
        <table className="timetable">
          <thead>
            <tr>
              <th>Faculty ID</th>
              <th>Name</th>
              <th>Department</th>
              <th>Email</th>
              <th>Mobile</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {faculty.map((f, i) => (
              <tr key={i}>
                <td>{f.userId}</td>
                <td>{f.name}</td>
                <td>{f.department}</td>
                <td>{f.email}</td>
                <td>{f.mobile}</td>
                <td style={{ color: "green" }}>{f.status}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
