import { useEffect, useState } from "react";
import axios from "axios";
import "../styles/leave.css";

export default function LeaveApply({ remainingCL }) {

  const user = JSON.parse(localStorage.getItem("erpUser"));

  const [leaveType, setLeaveType] = useState("");
  const [fromDate, setFromDate] = useState("");
  const [toDate, setToDate] = useState("");
  const [session, setSession] = useState("");
  const [reason, setReason] = useState("");

  const [classes, setClasses] = useState([]);
  const [facultyList, setFacultyList] = useState([]);
  const [showAdjustment, setShowAdjustment] = useState(false);

  useEffect(() => {
    axios.get("http://localhost:5000/api/users")
      .then(res => setFacultyList(res.data));
  }, []);

  const applyLeave = async () => {
    if (!leaveType || !fromDate || !toDate || !session || !reason) {
      alert("All fields are required");
      return;
    }

    if (leaveType === "CL" && remainingCL <= 0) {
      alert("CL not available. Select LOP.");
      return;
    }

    const res = await axios.get(
      `http://localhost:5000/api/timetable/faculty/${user.userId}`
    );

    if (res.data.length > 0) {
      setClasses(res.data);
      setShowAdjustment(true);
    } else {
      submitLeave([]);
    }
  };

  const submitLeave = async (adjustments) => {
    await axios.post("http://localhost:5000/api/leave/apply", {
      facultyId: user.userId,
      facultyName: user.username,
      designation: user.designation,
      leaveType,
      fromDate,
      toDate,
      session,
      reason,
      adjustments,
      status: "Pending-HOD"
    });

    alert("Leave applied successfully");
    setShowAdjustment(false);
  };

  return (
    <div className="leave-card">

      <h2 className="leave-heading">Leave Application</h2>

      {/* ===== FORM ===== */}
      <div className="leave-form-grid">

        <div>
          <label>Leave Type</label>
          <select onChange={e => setLeaveType(e.target.value)}>
            <option value="">Select</option>
            <option value="CL">Casual Leave (CL)</option>
            <option value="LOP">Loss of Pay (LOP)</option>
          </select>
          <small>Remaining CL: {remainingCL}</small>
        </div>

        <div>
          <label>Session</label>
          <select onChange={e => setSession(e.target.value)}>
            <option value="">Select</option>
            <option>FN</option>
            <option>AN</option>
            <option>Full Day</option>
          </select>
        </div>

        <div>
          <label>From Date</label>
          <input type="date" onChange={e => setFromDate(e.target.value)} />
        </div>

        <div>
          <label>To Date</label>
          <input type="date" onChange={e => setToDate(e.target.value)} />
        </div>

        <div className="full-width">
          <label>Reason for Leave</label>
          <textarea
            rows="3"
            placeholder="Enter reason"
            onChange={e => setReason(e.target.value)}
          />
        </div>

      </div>

      <div className="leave-btn-row">
        <button className="btn-primary" onClick={applyLeave}>
          Apply Leave
        </button>
      </div>

      {/* ===== ADJUSTMENT ===== */}
      {showAdjustment && (
        <div className="adjustment-section">

          <h3>Class Adjustment Required</h3>

          <table className="adjustment-table">
            <thead>
              <tr>
                <th>Date</th>
                <th>Hour</th>
                <th>Subject</th>
                <th>Adjust Faculty</th>
              </tr>
            </thead>
            <tbody>
              {classes.map((c, i) => (
                <tr key={i}>
                  <td>{c.date || "-"}</td>
                  <td>{c.hour}</td>
                  <td>{c.subject}</td>
                  <td>
                    <select
                      onChange={e => c.adjustedFacultyId = e.target.value}
                    >
                      <option value="">Select</option>
                      {facultyList.map(f => (
                        <option key={f.userId} value={f.userId}>
                          {f.username}
                        </option>
                      ))}
                    </select>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          <div className="leave-btn-row">
            <button
              className="btn-primary"
              onClick={() => submitLeave(classes)}
            >
              Submit with Adjustment
            </button>
          </div>

        </div>
      )}
    </div>
  );
}
