import { useEffect, useState } from "react";
import axios from "axios";

export default function HODLeaveApproval() {

  const user = JSON.parse(localStorage.getItem("erpUser"));
  const department = user?.department;

  const [leaves, setLeaves] = useState([]);

  const loadLeaves = async () => {
    try {
      const res = await axios.get(
        "http://localhost:5000/api/leave/pending",
        {
          params: {
            role: "HOD",
            department
          }
        }
      );
      setLeaves(res.data);
    } catch (err) {
      console.error(err);
      alert("Error loading leaves");
    }
  };

  useEffect(() => {
    loadLeaves();
  }, []);

  const approve = async (id) => {
    await axios.post(
      `http://localhost:5000/api/leave/approve/${id}`
    );
    loadLeaves();
  };

  const reject = async (id) => {
    await axios.post(
      `http://localhost:5000/api/leave/reject/${id}`
    );
    loadLeaves();
  };

  return (
    <div className="card">
      <h3>HOD Leave Approval</h3>

      {leaves.length === 0 ? (
        <p>No pending leaves</p>
      ) : (
        <table className="timetable">
          <thead>
            <tr>
              <th>Faculty</th>
              <th>From</th>
              <th>To</th>
              <th>Days</th>
              <th>Type</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {leaves.map(l => (
              <tr key={l._id}>
                <td>{l.facultyName}</td>
                <td>{l.fromDate}</td>
                <td>{l.toDate}</td>
                <td>{l.totalDays}</td>
                <td>{l.leaveType}</td>
                <td>
                  <button onClick={() => approve(l._id)}>Approve</button>
                  <button onClick={() => reject(l._id)}>Reject</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
