import { useEffect, useState } from "react";
import axios from "axios";
import "../styles/dashboard.css";

export default function AlternateLeaveApproval() {

  const user = JSON.parse(localStorage.getItem("erpUser"));
  const facultyId = user?.userId;

  const [leaves, setLeaves] = useState([]);
  const [loading, setLoading] = useState(true);

  /* =========================
     LOAD ALTERNATE REQUESTS
     ========================= */
  const loadRequests = async () => {
    try {
      const res = await axios.get(
        `http://localhost:5000/api/leave/alternate/${facultyId}`
      );
      setLeaves(res.data);
    } catch (err) {
      alert("Error loading alternate approvals");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadRequests();
  }, []);

  /* =========================
     APPROVE / REJECT
     ========================= */
  const handleAction = async (leaveId, action) => {
    try {
      const url =
        action === "approve"
          ? `/api/leave/approve/${leaveId}`
          : `/api/leave/reject/${leaveId}`;

      await axios.post(`http://localhost:5000${url}`);
      alert(`Leave ${action}d successfully`);
      loadRequests();

    } catch (err) {
      alert("Action failed");
    }
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div className="card">
      <h3>Alternate Faculty Approval</h3>

      {leaves.length === 0 ? (
        <p>No pending alternate approvals</p>
      ) : (
        leaves.map(leave => (
          <div key={leave._id} className="card" style={{ marginBottom: 15 }}>

            <p><strong>Faculty:</strong> {leave.facultyName}</p>
            <p><strong>Leave:</strong> {leave.fromDate} to {leave.toDate}</p>

            <table className="timetable">
              <thead>
                <tr>
                  <th>Date</th>
                  <th>Department</th>
                  <th>Year</th>
                  <th>Hour</th>
                  <th>Subject</th>
                </tr>
              </thead>
              <tbody>
                {leave.adjustments
                  .filter(a => a.adjustedFacultyId === facultyId)
                  .map((a, i) => (
                    <tr key={i}>
                      <td>{a.date}</td>
                      <td>{leave.department}</td>
                      <td>{a.section.split(" ")[1]}</td>
                      <td>{a.hour}</td>
                      <td>{a.subject}</td>
                    </tr>
                  ))}
              </tbody>
            </table>

            <div style={{ marginTop: 10 }}>
              <button
                className="btn-primary"
                onClick={() => handleAction(leave._id, "approve")}
              >
                Approve
              </button>

              <button
                className="btn-danger"
                style={{ marginLeft: 10 }}
                onClick={() => handleAction(leave._id, "reject")}
              >
                Reject
              </button>
            </div>

          </div>
        ))
      )}
    </div>
  );
}
