import { useState } from "react";
import LeaveCheck from "./LeaveCheck";
import LeaveApply from "./LeaveApply";
import "../styles/dashboard.css";

export default function LeaveManagement() {
  const [activeTab, setActiveTab] = useState("check");

  return (
    <div className="card">

      <h3 style={{ marginBottom: "15px" }}>Leave Management</h3>

      {/* SUB MENU */}
      <div className="leave-menu">
        <button
          className={activeTab === "check" ? "leave-btn active" : "leave-btn"}
          onClick={() => setActiveTab("check")}
        >
          Leave Check
        </button>

        <button
          className={activeTab === "apply" ? "leave-btn active" : "leave-btn"}
          onClick={() => setActiveTab("apply")}
        >
          Leave Apply
        </button>
      </div>

      {/* CONTENT */}
      <div style={{ marginTop: "20px" }}>
        {activeTab === "check" && <LeaveCheck />}
        {activeTab === "apply" && <LeaveApply />}
      </div>

    </div>
  );
}
