import { useState } from "react";

export default function StudentAttendance() {
  const [dept, setDept] = useState("");
  const [year, setYear] = useState("");
  const [hour, setHour] = useState("");

  // Simulating timetable-based hours
  const timetableHours = ["2", "4"];

  return (
    <div className="card">
      <h3>Student Attendance</h3>

      <label>Department</label>
      <select onChange={e => setDept(e.target.value)}>
        <option value="">Select</option>
        <option value="CSE">CSE</option>
        <option value="IT">IT</option>
      </select>

      <label>Year</label>
      <select onChange={e => setYear(e.target.value)}>
        <option value="">Select</option>
        <option value="2">II</option>
        <option value="3">III</option>
      </select>

      <label>Hour (From My Timetable)</label>
      <select onChange={e => setHour(e.target.value)}>
        <option value="">Select Hour</option>
        {timetableHours.map(h => (
          <option key={h}>{h}</option>
        ))}
      </select>

      <button disabled={!hour}>Mark Attendance</button>
    </div>
  );
}
