import { useState } from "react";
import AdminCsvUpload from "../components/AdminCsvUpload";
import "../styles/dashboard.css";

export default function AdminDashboard() {
  const [page, setPage] = useState("dashboard");

  return (
    <div className="erp-layout">

      {/* ===== SIDEBAR ===== */}
      <aside className="erp-sidebar">
        <h2 className="erp-logo">EduERP</h2>

        <ul>
          <li onClick={() => setPage("dashboard")}>Dashboard</li>
          <li onClick={() => setPage("upload")}>Upload Timetable</li>
          <li onClick={() => setPage("reports")}>Attendance Reports</li>
          <li onClick={() => setPage("pdf")}>Generate PDF</li>
          <li>Logout</li>
        </ul>
      </aside>

      {/* ===== MAIN AREA ===== */}
      <div className="erp-main">

        {/* HEADER */}
        <header className="erp-header">
          <h3>Admin Dashboard</h3>
          <span>Welcome, Admin</span>
        </header>

        {/* CONTENT */}
        <div className="erp-content">

          {/* DASHBOARD HOME */}
          {page === "dashboard" && (
            <div className="dashboard-grid">
              <div className="card">
                <h4>Timetable Management</h4>
                <p>Upload & manage faculty timetables using CSV</p>
              </div>

              <div className="card">
                <h4>Attendance Monitoring</h4>
                <p>View class-wise & faculty-wise attendance</p>
              </div>

              <div className="card">
                <h4>Reports & PDF</h4>
                <p>Generate Anna University attendance registers</p>
              </div>
            </div>
          )}

          {/* CSV UPLOAD */}
          {page === "upload" && (
            <div className="card">
              <h4>Upload Timetable CSV</h4>
              <p>Upload faculty timetable in CSV format</p>
              <AdminCsvUpload />
            </div>
          )}

          {/* ATTENDANCE REPORTS */}
          {page === "reports" && (
            <div className="card">
              <h4>Attendance Reports</h4>
              <p>Faculty-wise and class-wise attendance reports will appear here.</p>
            </div>
          )}

          {/* PDF GENERATION */}
          {page === "pdf" && (
            <div className="card">
              <h4>Generate Attendance PDF</h4>
              <p>Generate Anna University format attendance register.</p>
              <a
                href="http://localhost:5000/api/attendance/pdf"
                target="_blank"
                rel="noreferrer"
              >
                <button className="btn-primary">Generate PDF</button>
              </a>
            </div>
          )}

        </div>
      </div>
    </div>
  );
}
