import { useState } from "react";

export default function StudentAttendance() {
  const timetable = [
    { subject: "DBMS", hour: 2 },
    { subject: "OS", hour: 4 }
  ];

  const [subject, setSubject] = useState("");
  const [hour, setHour] = useState("");

  const onSubjectChange = (s) => {
    setSubject(s);
    const found = timetable.find(t => t.subject === s);
    setHour(found ? found.hour : "");
  };

  return (
    <div className="card" style={{ maxWidth: "700px" }}>
      <h3 style={{ textAlign: "center" }}>Student Attendance</h3>

      <div style={{ display: "grid", gridTemplateColumns: "200px 1fr", gap: "15px" }}>
        <label>Department</label>
        <select><option>CSE</option></select>

        <label>Year</label>
        <select><option>II</option></select>

        <label>Subject</label>
        <select onChange={e => onSubjectChange(e.target.value)}>
          <option>Select</option>
          {timetable.map(t => (
            <option key={t.subject}>{t.subject}</option>
          ))}
        </select>

        <label>Hour</label>
        <select disabled>
          <option>{hour}</option>
        </select>
      </div>

      <div style={{ textAlign: "right", marginTop: "20px" }}>
        <button
          style={{
            padding: "10px 25px",
            background: "#2563eb",
            color: "#fff",
            border: "none",
            borderRadius: "6px"
          }}
        >
          Mark Attendance
        </button>
      </div>
    </div>
  );
}
