import { useEffect, useState } from "react";
import axios from "axios";
import "../styles/faculty.css";

export default function FacultyList() {

  const [users, setUsers] = useState([]);
  const [showForm, setShowForm] = useState(false);

  const [form, setForm] = useState({
    userId: "",
    username: "",
    designation: "",
    department: "",
    email: "",
    password: ""
  });

  /* =========================
     LOAD FACULTY USERS
     ========================= */
  const loadUsers = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/users");
      setUsers(res.data.filter(u => u.role === "faculty"));
    } catch (err) {
      console.error("Error loading users", err);
    }
  };

  useEffect(() => {
    loadUsers();
  }, []);

  /* =========================
     CREATE FACULTY USER
     ========================= */
  const handleCreate = async () => {
    if (!form.userId || !form.username || !form.department || !form.designation) {
      alert("User ID, User Name, Designation and Department are required");
      return;
    }

    try {
      await axios.post("http://localhost:5000/api/users/create", {
        ...form,
        role: "faculty"
      });

      alert(`Faculty user created successfully\nDefault Password: ${form.password}`);

      setForm({
        userId: "",
        username: "",
        designation: "",
        department: "",
        email: "",
        password: ""
      });

      setShowForm(false);
      loadUsers();

    } catch (err) {
      alert(err.response?.data?.message || "Error creating faculty user");
    }
  };

  return (
    <div className="card faculty-container">

      {/* HEADER */}
      <div className="faculty-header">
        <h3>Faculty Management</h3>
        <button
          className="btn-primary"
          onClick={() => setShowForm(!showForm)}
        >
          {showForm ? "Close" : "+ Create Faculty"}
        </button>
      </div>

      {/* CREATE FORM */}
      {showForm && (
        <div className="faculty-form">

          <input
            type="text"
            placeholder="User ID (Eg: F001)"
            value={form.userId}
            onChange={e => setForm({ ...form, userId: e.target.value })}
          />

          <input
            type="text"
            placeholder="User Name"
            value={form.username}
            onChange={e => setForm({ ...form, username: e.target.value })}
          />

          <select
            value={form.designation}
            onChange={e => setForm({ ...form, designation: e.target.value })}
          >
            <option value="">Select Designation</option>
            <option>Assistant Professor</option>
            <option>Associate Professor</option>
            <option>Professor</option>
            <option>HOD</option>
            <option>Vice Principal</option>
            <option>Principal</option>
          </select>

          <input
            type="text"
            placeholder="Department"
            value={form.department}
            onChange={e => setForm({ ...form, department: e.target.value })}
          />

          <input
            type="email"
            placeholder="Email"
            value={form.email}
            onChange={e => setForm({ ...form, email: e.target.value })}
          />

          <input
            type="password"
            placeholder="Password"
            value={form.password}
            onChange={e => setForm({ ...form, password: e.target.value })}
          />

          <button className="btn-primary" onClick={handleCreate}>
            Save Faculty
          </button>
        </div>
      )}

      {/* FACULTY TABLE */}
      {users.length === 0 ? (
        <p className="no-data">No faculty records found</p>
      ) : (
        <table className="faculty-table">
          <thead>
            <tr>
              <th>User ID</th>
              <th>User Name</th>
              <th>Designation</th>
              <th>Department</th>
              <th>Email</th>
            </tr>
          </thead>

          <tbody>
            {users.map((u, index) => (
              <tr key={index}>
                <td>{u.userId}</td>
                <td>{u.username}</td>
                <td>{u.designation}</td>
                <td>{u.department}</td>
                <td>{u.email || "-"}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

    </div>
  );
}
