import { useState } from "react";
import "../styles/dashboard.css";

/* Convert selected date → day name */
const getDayFromDate = (dateStr) => {
  const days = [
    "Sunday",
    "Monday",
    "Tuesday",
    "Wednesday",
    "Thursday",
    "Friday",
    "Saturday"
  ];
  return days[new Date(dateStr).getDay()];
};

export default function LeaveApply() {

  const [leaveType, setLeaveType] = useState("");
  const [date, setDate] = useState("");
  const [session, setSession] = useState("");
  const [showAdjustment, setShowAdjustment] = useState(false);
  const [adjustmentClasses, setAdjustmentClasses] = useState([]);

  /* ===== SAMPLE DATA (REPLACE WITH DB LATER) ===== */

  const remainingCL = 1; // from LeaveCheck / DB

  const facultyTimetable = [
    { day: "Friday", hour: 5, subject: "DBMS", section: "CSE II A" },
    { day: "Friday", hour: 6, subject: "DBMS", section: "CSE II A" },
    { day: "Friday", hour: 7, subject: "DBMS", section: "CSE II A" }
  ];

  /* ===== HANDLERS ===== */

  const handleLeaveTypeChange = (value) => {
    if (value === "CL" && remainingCL <= 0) {
      alert("CL not available. Please select LOP.");
      return;
    }
    setLeaveType(value);
  };

  const handleApply = () => {
    if (!leaveType || !date || !session) {
      alert("Please fill all fields");
      return;
    }

    const leaveDay = getDayFromDate(date);

    // Filter timetable based on SELECTED DATE
    const dayClasses = facultyTimetable.filter(
      t => t.day === leaveDay
    );

    if (dayClasses.length > 0) {
      setAdjustmentClasses(dayClasses);
      setShowAdjustment(true);
    } else {
      alert(`Leave applied successfully. No classes on ${leaveDay}.`);
      setShowAdjustment(false);
      setAdjustmentClasses([]);
    }
  };

  return (
    <div className="leave-container">

      <h3 className="leave-title">Leave Application</h3>

      {/* ===== LEAVE FORM ===== */}
      <div className="leave-form">

        <div className="leave-row">
          <label>Leave Type</label>
          <select onChange={(e) => handleLeaveTypeChange(e.target.value)}>
            <option value="">Select</option>
            <option value="CL">Casual Leave (CL)</option>
            <option value="LOP">Loss of Pay (LOP)</option>
          </select>
        </div>

        <div className="leave-row">
          <label>Date</label>
          <input type="date" onChange={(e) => setDate(e.target.value)} />
        </div>

        <div className="leave-row">
          <label>Session</label>
          <select onChange={(e) => setSession(e.target.value)}>
            <option value="">Select</option>
            <option>FN</option>
            <option>AN</option>
            <option>Full Day</option>
          </select>
        </div>

        <div className="leave-actions">
          <button className="btn-primary" onClick={handleApply}>
            Apply Leave
          </button>
        </div>
      </div>

      {/* ===== ADJUSTMENT SECTION ===== */}
      {showAdjustment && (
        <div className="adjustment-box">

          <h4>Class Adjustment Required</h4>

          <table className="timetable">
            <thead>
              <tr>
                <th>Hour</th>
                <th>Subject</th>
                <th>Section</th>
                <th>Adjusting Faculty</th>
              </tr>
            </thead>
            <tbody>
              {adjustmentClasses.map((t, i) => (
                <tr key={i}>
                  <td>{t.hour}</td>
                  <td>{t.subject}</td>
                  <td>{t.section}</td>
                  <td>
                    <select>
                      <option>Select Faculty</option>
                      <option>Dr. Anand</option>
                      <option>Ms. Priya</option>
                      <option>Mr. Ramesh</option>
                    </select>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          <div className="leave-actions">
            <button className="btn-primary">
              Submit Leave with Adjustment
            </button>
          </div>
        </div>
      )}

    </div>
  );
}
