import { useEffect, useState } from "react";
import axios from "axios";

export default function LeaveCheck() {

  const user = JSON.parse(localStorage.getItem("erpUser"));
  const [data, setData] = useState(null);

  const load = async () => {
    const res = await axios.get(
      `http://localhost:5000/api/leave/summary/${user.userId}`
    );
    setData(res.data);
  };

  useEffect(() => { load(); }, []);

  const cancelLeave = async (id) => {
    if (!window.confirm("Cancel leave?")) return;
    await axios.put(`http://localhost:5000/api/leave/cancel/${id}`);
    load();
  };

  if (!data) return null;

  return (
    <div className="card">
      <h3>Leave Summary</h3>

      <p>Total CL: {data.totalCL}</p>
      <p>Used CL: {data.usedCL}</p>
      <p>Remaining CL: {data.remainingCL}</p>

      <h4>Leave History</h4>

      <table>
        <thead>
          <tr>
            <th>From</th>
            <th>To</th>
            <th>Type</th>
            <th>Status</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {data.leaves.map(l => (
            <tr key={l._id}>
              <td>{l.fromDate}</td>
              <td>{l.toDate}</td>
              <td>{l.leaveType}</td>
              <td>{l.status}</td>
              <td>
                {["Pending-HOD", "Pending-Principal"].includes(l.status) && (
                  <button onClick={() => cancelLeave(l._id)}>Cancel</button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
