import { useEffect, useState } from "react";
import axios from "axios";
import "../styles/dashboard.css";

export default function StudentAttendance() {
  const [day, setDay] = useState("");
  const [timetable, setTimetable] = useState([]);
  const [hours, setHours] = useState([]);

  // Hardcoded facultyId for now (later from login)
  const facultyId = "F001";

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/timetable/faculty/${facultyId}`)
      .then(res => setTimetable(res.data));
  }, []);

  // When day changes → recalculate hours
  useEffect(() => {
    if (!day) return;

    const dayEntries = timetable.filter(t => t.day === day);

    let resultHours = [];

    dayEntries.forEach(entry => {
      for (let i = entry.periodStart; i <= entry.periodEnd; i++) {
        resultHours.push({
          period: i,
          subject: entry.subject
        });
      }
    });

    setHours(resultHours);
  }, [day, timetable]);

  return (
    <div className="card">
      <h3>Student Attendance</h3>

      {/* DAY SELECTION */}
      <select value={day} onChange={e => setDay(e.target.value)}>
        <option value="">Select Day</option>
        <option>Monday</option>
        <option>Tuesday</option>
        <option>Wednesday</option>
        <option>Thursday</option>
        <option>Friday</option>
        <option>Saturday</option>
      </select>

      {/* HOURS DISPLAY */}
      {hours.length > 0 && (
        <table className="timetable" style={{ marginTop: 20 }}>
          <thead>
            <tr>
              <th>Hour</th>
              <th>Subject</th>
              <th>Mark Attendance</th>
            </tr>
          </thead>
          <tbody>
            {hours.map((h, index) => (
              <tr key={index}>
                <td>{h.period}</td>
                <td>{h.subject}</td>
                <td>
                  <button className="btn-primary">Mark</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {day && hours.length === 0 && (
        <p style={{ marginTop: 20, color: "red" }}>
          No classes scheduled on {day}
        </p>
      )}
    </div>
  );
}
