import { useEffect, useState } from "react";
import axios from "axios";
import "../styles/timetable.css";

export default function FacultyTimetable() {
  const [data, setData] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/timetable/faculty/F001")
      .then(res => setData(res.data));
  }, []);

  const days = ["Monday","Tuesday","Wednesday","Thursday","Friday","Saturday"];

  return (
    <table className="timetable">
      <thead>
        <tr>
          <th>Day</th>
          {[1,2,3,4,5,6,7].map(p => <th key={p}>P{p}</th>)}
        </tr>
      </thead>
      <tbody>
        {days.map(day => (
          <tr key={day}>
            <td>{day}</td>
            {[1,2,3,4,5,6,7].map(p => {
              const cell = data.find(
                d => d.day === day && d.periodStart === p
              );
              if (!cell) return <td key={p}></td>;
              return (
                <td key={p} colSpan={cell.periodEnd - cell.periodStart + 1}>
                  {cell.subject}
                </td>
              );
            })}
          </tr>
        ))}
      </tbody>
    </table>
  );
}
