import { useEffect, useState } from "react";
import axios from "axios";

export default function HODLeaveApproval() {

  const user = JSON.parse(localStorage.getItem("erpUser"));
  const [leaves, setLeaves] = useState([]);
  const [reason, setReason] = useState("");
  const [rejectId, setRejectId] = useState(null);

  const loadLeaves = async () => {
    try {
      const res = await axios.get(
        "http://localhost:5000/api/leave/hod",
        {
          params: { department: user.department }
        }
      );
      setLeaves(res.data);
    } catch (err) {
      console.error(err);
      alert("Error fetching leave");
    }
  };

  useEffect(() => {
    loadLeaves();
  }, []);

  const approve = async (id) => {
    try {
      const res = await axios.post(
        `http://localhost:5000/api/leave/hod/approve/${id}`
      );
      alert(res.data.message);
      loadLeaves();
    } catch {
      alert("Approval failed");
    }
  };

  const reject = async () => {
    try {
      const res = await axios.post(
        `http://localhost:5000/api/leave/hod/reject/${rejectId}`,
        { reason }
      );
      alert(res.data.message);
      setRejectId(null);
      setReason("");
      loadLeaves();
    } catch {
      alert("Reject failed");
    }
  };

  return (
    <div className="card">
      <h3>HOD Leave Approval</h3>

      {leaves.length === 0 ? (
        <p>No pending leaves</p>
      ) : (
        <table className="timetable">
          <thead>
            <tr>
              <th>Faculty</th>
              <th>From</th>
              <th>To</th>
              <th>Type</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {leaves.map(l => (
              <tr key={l._id}>
                <td>{l.facultyName}</td>
                <td>{l.fromDate}</td>
                <td>{l.toDate}</td>
                <td>{l.leaveType}</td>
                <td>
                  <button onClick={() => approve(l._id)}>Approve</button>
                  <button onClick={() => setRejectId(l._id)}>Reject</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {rejectId && (
        <div style={{ marginTop: 10 }}>
          <textarea
            placeholder="Enter rejection reason"
            value={reason}
            onChange={e => setReason(e.target.value)}
          />
          <br />
          <button onClick={reject}>Submit Rejection</button>
        </div>
      )}
    </div>
  );
}
