import { useState } from "react";
import "../styles/app.css";

export default function StudentAttendance() {

  // Sample timetable (later this will come from backend)
  const timetableData = [
    { day: "Monday", hour: 2, subject: "DBMS", dept: "CSE", year: "II" },
    { day: "Monday", hour: 4, subject: "OS", dept: "CSE", year: "II" }
  ];

  const [department, setDepartment] = useState("");
  const [year, setYear] = useState("");
  const [subject, setSubject] = useState("");
  const [hour, setHour] = useState("");

  const subjects = [...new Set(timetableData.map(t => t.subject))];

  const handleSubjectChange = (sub) => {
    setSubject(sub);
    const h = timetableData.find(t => t.subject === sub)?.hour || "";
    setHour(h);
  };

  return (
    <div className="attendance-wrapper">
      <form className="attendance-card">

        <h2>Student Attendance</h2>

        <div className="form-row">
          <label>Department</label>
          <select value={department} onChange={e => setDepartment(e.target.value)}>
            <option value="">Select</option>
            <option value="CSE">CSE</option>
            <option value="IT">IT</option>
          </select>
        </div>

        <div className="form-row">
          <label>Year</label>
          <select value={year} onChange={e => setYear(e.target.value)}>
            <option value="">Select</option>
            <option value="II">II</option>
            <option value="III">III</option>
          </select>
        </div>

        <div className="form-row">
          <label>Subject (From My Timetable)</label>
          <select value={subject} onChange={e => handleSubjectChange(e.target.value)}>
            <option value="">Select</option>
            {subjects.map(sub => (
              <option key={sub} value={sub}>{sub}</option>
            ))}
          </select>
        </div>

        <div className="form-row">
          <label>Hour (Auto)</label>
          <select value={hour} disabled>
            <option>{hour}</option>
          </select>
        </div>

        <button type="button" className="primary-btn">
          Mark Attendance
        </button>

      </form>
    </div>
  );
}
