import { useEffect, useState } from "react";
import axios from "axios";
import "../styles/dashboard.css";

export default function SecretaryLeaveApproval() {

  const [leaves, setLeaves] = useState([]);
  const [loading, setLoading] = useState(true);

  /* ===== LOAD LEAVES PENDING SECRETARY APPROVAL ===== */
  useEffect(() => {
    loadLeaves();
  }, []);

  const loadLeaves = async () => {
    try {
      const res = await axios.get(
        "http://localhost:5000/api/leave/pending/secretary"
      );
      setLeaves(res.data || []);
      setLoading(false);
    } catch (err) {
      console.error(err);
      setLoading(false);
    }
  };

  /* ===== APPROVE / REJECT ===== */
  const updateStatus = async (leaveId, status) => {
    try {
      await axios.put(
        `http://localhost:5000/api/leave/approve/${leaveId}`,
        { status }
      );
      alert(`Leave ${status}`);
      loadLeaves();
    } catch (err) {
      alert("Action failed");
    }
  };

  return (
    <div className="card">
      <h3>Secretary Leave Approval</h3>

      {loading ? (
        <p>Loading...</p>
      ) : leaves.length === 0 ? (
        <p>No leave requests pending</p>
      ) : (
        <table className="timetable">
          <thead>
            <tr>
              <th>Faculty</th>
              <th>Designation</th>
              <th>Department</th>
              <th>Leave Type</th>
              <th>From</th>
              <th>To</th>
              <th>Days</th>
              <th>Reason</th>
              <th>Status</th>
              <th>Action</th>
            </tr>
          </thead>

          <tbody>
            {leaves.map(l => (
              <tr key={l._id}>
                <td>{l.facultyName}</td>
                <td>{l.designation}</td>
                <td>{l.department}</td>
                <td>{l.leaveType}</td>
                <td>{l.fromDate}</td>
                <td>{l.toDate}</td>
                <td>{l.totalDays}</td>
                <td>{l.reason}</td>
                <td>{l.status}</td>
                <td>
                  <button
                    className="btn-primary"
                    onClick={() => updateStatus(l._id, "Approved")}
                  >
                    Approve
                  </button>
                  <button
                    className="btn-danger"
                    onClick={() => updateStatus(l._id, "Rejected")}
                  >
                    Reject
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
